import React, { useState, useEffect } from 'react';
import {
  Alert,
  Box,
  Button,
  CircularProgress,
  FormControl,
  Grid,
  InputLabel,
  MenuItem,
  Paper,
  Select,
  SelectChangeEvent,
  Typography,
} from '@mui/material';
import { User, IndividualStatsData, loadUsers, loadIndividualStats } from '../utils/database';

const IndividualStats: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [dbBuffer, setDbBuffer] = useState<ArrayBuffer | null>(null);
  const [dbKey, setDbKey] = useState<string | undefined>(undefined);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [users, setUsers] = useState<User[]>([]);
  const [selectedUser, setSelectedUser] = useState<string>('');
  const [statsData, setStatsData] = useState<IndividualStatsData | null>(null);

  useEffect(() => {
    if (selectedUser && dbBuffer) {
      fetchIndividualStats(selectedUser);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selectedUser]);

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>): void => {
    const selectedFile = e.target.files?.[0] ?? null;
    setFile(selectedFile);
    setUsers([]);
    setSelectedUser('');
    setStatsData(null);
    setError(null);
    setDbBuffer(null);
    setDbKey(undefined);
    if (selectedFile) {
      selectedFile.arrayBuffer().then(buffer => {
        setDbBuffer(buffer);
      });
    }
  };

  const handleUploadAndAnalyse = async (): Promise<void> => {
    if (!dbBuffer) return;

    setLoading(true);
    setError(null);
    setUsers([]);

    try {
      const userList = await loadUsers(dbBuffer, undefined);
      setUsers(userList);
      setDbKey(undefined);
    } catch (err) {
      if (err instanceof Error && err.message.includes('Please provide a key')) {
        const key = window.prompt('This database appears to be encrypted. Please provide your key:');
        if (key) {
          setDbKey(key);
          try {
            const userList = await loadUsers(dbBuffer, key);
            setUsers(userList);
          } catch (secondErr) {
            const msg = secondErr instanceof Error ? secondErr.message : 'An unknown error occurred during decryption.';
            setError(msg);
          }
        } else {
          setError('An encryption key is required to process this file.');
        }
      } else {
        const msg = err instanceof Error ? err.message : 'An unknown error occurred.';
        setError(msg);
      }
    } finally {
      setLoading(false);
    }
  };

  const fetchIndividualStats = async (userId: string) => {
    if (!dbBuffer) return;

    setLoading(true);
    setError(null);
    try {
      const stats = await loadIndividualStats(dbBuffer, dbKey, userId);
      setStatsData(stats);
    } catch (err) {
      const msg = err instanceof Error ? err.message : 'An unknown error occurred.';
      setError(msg);
    } finally {
      setLoading(false);
    }
  };

  const handleUserChange = (event: SelectChangeEvent<string>) => {
    setSelectedUser(event.target.value);
  };

  const renderKpiCard = (title: string, value: string | number) => (
    <Grid item xs={12} sm={4}>
      <Paper sx={{ p: 2, textAlign: 'center' }}>
        <Typography variant="h4" component="div">{value}</Typography>
        <Typography variant="body1" color="text.secondary">{title}</Typography>
      </Paper>
    </Grid>
  );

  return (
    <Box sx={{ p: 4 }}>
      <Typography variant="h4" gutterBottom>
        Individual Stats
      </Typography>

      <Box sx={{ display: 'flex', gap: 2, alignItems: 'center', mb: 4 }}>
        <Button variant="contained" component="label" disabled={loading}>
          Select Database
          <input type="file" accept=".db,.sqlite" hidden onChange={handleFileChange} />
        </Button>
        <Button variant="contained" color="secondary" onClick={handleUploadAndAnalyse} disabled={loading || !file}>
          {loading && !statsData ? 'Loading Usersâ€¦' : 'Load Users'}
        </Button>
        {loading && <CircularProgress size={24} />}
      </Box>

      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

      {users.length > 0 && (
        <FormControl fullWidth sx={{ mb: 4 }}>
          <InputLabel id="user-select-label">Select User</InputLabel>
          <Select
            labelId="user-select-label"
            value={selectedUser}
            label="Select User"
            onChange={handleUserChange}
          >
            {users.map(user => (
              <MenuItem key={user.id} value={user.id}>
                {user.name}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      )}

      {statsData && (
        <Grid container spacing={3}>
          {renderKpiCard('Total Messages Sent', statsData.totalMessagesSent)}
          {renderKpiCard('Most Popular Day', statsData.mostPopularDay)}
          {renderKpiCard('Total Reactions Sent', statsData.totalReactionsSent)}
        </Grid>
      )}
    </Box>
  );
};

export default IndividualStats;
