import React, { useState, useEffect, useRef } from 'react';
import {
  Alert,
  Box,
  Button,
  Checkbox,
  CircularProgress,
  Grid,
  ListItemText,
  MenuItem,
  Paper,
  Select,
  SelectChangeEvent,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
} from '@mui/material';
import {
  ResponsiveContainer,
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
} from 'recharts';

import { loadDatabase, Conversation, AnalyticsData } from '../utils/database';

// -----------------------------------------------------------------------------
// Types
// -----------------------------------------------------------------------------

// -----------------------------------------------------------------------------
// Component
// -----------------------------------------------------------------------------
const Dashboard: React.FC = () => {
  // ---------------------------------------------------------------------------
  // State
  // ---------------------------------------------------------------------------
  const [file, setFile] = useState<File | null>(null);
  const [dbBuffer, setDbBuffer] = useState<ArrayBuffer | null>(null);
  const [dbKey, setDbKey] = useState<string | undefined>(undefined);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [analyticsData, setAnalyticsData] = useState<AnalyticsData | null>(null);
  const [selectedConversations, setSelectedConversations] = useState<string[]>([]);

  const isInitialMount = useRef(true);

  // ---------------------------------------------------------------------------
  // Effects
  // ---------------------------------------------------------------------------
  useEffect(() => {
    if (isInitialMount.current) {
      isInitialMount.current = false;
      return;
    }

    if (dbBuffer) {
      runAnalysis();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selectedConversations]);

  // ---------------------------------------------------------------------------
  // Helpers
  // ---------------------------------------------------------------------------
  const formatHour = (hour: number): string => `${hour}:00`;

  const runAnalysis = async () => {
    if (!dbBuffer) return;

    setLoading(true);
    setError(null);

    try {
      const processed = await loadDatabase(dbBuffer, dbKey, selectedConversations);
      setAnalyticsData(prevData => ({
        ...processed,
        all_conversations: prevData?.all_conversations || processed.all_conversations,
      }));
    } catch (err) {
      const msg = err instanceof Error ? err.message : 'An unknown error occurred.';
      setError(msg);
    } finally {
      setLoading(false);
    }
  };

  // ---------------------------------------------------------------------------
  // Handlers
  // ---------------------------------------------------------------------------
  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>): void => {
    const selectedFile = e.target.files?.[0] ?? null;
    setFile(selectedFile);
    setAnalyticsData(null);
    setError(null);
    setSelectedConversations([]);
    setDbBuffer(null);
    setDbKey(undefined);
    if (selectedFile) {
      selectedFile.arrayBuffer().then(buffer => {
        setDbBuffer(buffer);
      });
    }
  };

  const handleFilterChange = (event: SelectChangeEvent<string[]>) => {
    const { target: { value } } = event;
    setSelectedConversations(
      typeof value === 'string' ? value.split(',') : value
    );
  };

  const handleUploadAndAnalyse = async (): Promise<void> => {
    if (!dbBuffer) return;

    setLoading(true);
    setError(null);

    try {
      const processed = await loadDatabase(dbBuffer, undefined, selectedConversations);
      setAnalyticsData(processed);
      setDbKey(undefined);
    } catch (err) {
      if (err instanceof Error && err.message.includes('Please provide a key')) {
        const key = window.prompt('This database appears to be encrypted. Please provide your key:');
        if (key) {
          setDbKey(key);
          try {
            const processed = await loadDatabase(dbBuffer, key, selectedConversations);
            setAnalyticsData(processed);
          } catch (secondErr) {
            const msg = secondErr instanceof Error ? secondErr.message : 'An unknown error occurred during decryption.';
            setError(msg);
          }
        } else {
          setError('An encryption key is required to process this file.');
        }
      } else {
        const msg = err instanceof Error ? err.message : 'An unknown error occurred.';
        setError(msg);
      }
    } finally {
      setLoading(false);
    }
  };

  // ---------------------------------------------------------------------------
  // Render helpers
  // ---------------------------------------------------------------------------
  const renderKpiCard = (title: string, value: string | number) => (
    <Grid item xs={12} sm={4}>
      <Paper sx={{ p: 2, textAlign: 'center' }}>
        <Typography variant="h4" component="div">{value}</Typography>
        <Typography variant="body1" color="text.secondary">{title}</Typography>
      </Paper>
    </Grid>
  );

  const renderConversationFilter = () => (
    <Paper sx={{ p: 2, mb: 4 }}>
      <Typography variant="h6" gutterBottom>Filter by Conversation</Typography>
      <Select
        multiple
        value={selectedConversations}
        onChange={handleFilterChange}
        fullWidth
        disabled={!analyticsData}
        renderValue={(selected: string[]) => {
          if (selected.length === 0) return <em>All Conversations</em>;
          const selectedNames = selected.map(id => {
            const convo = analyticsData?.all_conversations.find(c => c.id === id);
            return convo ? convo.name : id;
          });
          return selectedNames.join(', ');
        }}
      >
        {analyticsData?.all_conversations.map((convo: Conversation) => (
          <MenuItem key={convo.id} value={convo.id}>
            <Checkbox checked={selectedConversations.indexOf(convo.id) > -1} />
            <ListItemText primary={convo.name} />
          </MenuItem>
        ))}
      </Select>
    </Paper>
  );

  const renderKpiSummary = () => (
    <Grid container spacing={3} sx={{ mb: 4 }}>
      {renderKpiCard('Total Messages', analyticsData?.kpis.total_messages ?? '...')}
      {renderKpiCard('Total Conversations', analyticsData?.kpis.total_conversations ?? '...')}
      {renderKpiCard('Avg Messages / Day', analyticsData?.kpis.avg_messages_per_day ?? '...')}
    </Grid>
  );

  const renderDailyChart = () => (
    <Paper sx={{ p: 2, height: 400 }}>
      <Typography variant="h6" gutterBottom>Daily Message Activity</Typography>
      {analyticsData?.message_counts?.by_day ? (
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={Object.entries(analyticsData.message_counts.by_day)}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="0" />
            <YAxis />
            <Tooltip />
            <Line type="monotone" dataKey="1" stroke="#8884d8" />
          </LineChart>
        </ResponsiveContainer>
      ) : <Typography variant="body2" color="text.secondary">No data</Typography>}
    </Paper>
  );

  const renderTopConversations = () => (
    <Paper sx={{ p: 2, height: 'calc(100% - 32px)', display: 'flex', flexDirection: 'column' }}>
      <Typography variant="h6" gutterBottom>Top Conversations</Typography>
      {analyticsData?.top_conversations && analyticsData.top_conversations.length > 0 ? (
        <TableContainer sx={{ flexGrow: 1 }}>
          <Table stickyHeader size="small">
            <TableHead>
              <TableRow>
                <TableCell>Conversation</TableCell>
                <TableCell align="right">Messages</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {analyticsData.top_conversations.map((convo) => (
                <TableRow key={convo.name}>
                  <TableCell component="th" scope="row">{convo.name}</TableCell>
                  <TableCell align="right">{convo.count}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      ) : <Typography variant="body2" color="text.secondary">No data</Typography>}
    </Paper>
  );

  const renderReactionAnalytics = () => (
    <Grid container spacing={3} sx={{ mt: 4 }}>
      <Grid item xs={12}>
        <Typography variant="h5" gutterBottom>Reaction Analytics</Typography>
      </Grid>
      <Grid item xs={12} md={4}>
        <Paper sx={{ p: 2, height: '100%', textAlign: 'center' }}>
          <Typography variant="h4" component="div">{analyticsData?.reactions.total_reactions ?? '...'}</Typography>
          <Typography variant="body1" color="text.secondary">Total Reactions</Typography>
        </Paper>
      </Grid>
      <Grid item xs={12} md={8}>
        <Paper sx={{ p: 2 }}>
          <Typography variant="h6" gutterBottom>Top 10 Emojis</Typography>
          {analyticsData?.reactions?.top_emojis && analyticsData.reactions.top_emojis.length > 0 ? (
            <TableContainer>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Emoji</TableCell>
                    <TableCell align="right">Count</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {analyticsData.reactions.top_emojis.map((reaction) => (
                    <TableRow key={reaction.emoji}>
                      <TableCell component="th" scope="row">{reaction.emoji}</TableCell>
                      <TableCell align="right">{reaction.count}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          ) : <Typography variant="body2" color="text.secondary">No data</Typography>}
        </Paper>
      </Grid>
      <Grid item xs={12}>
        <Paper sx={{ p: 2, mt: 3 }}>
          <Typography variant="h6" gutterBottom>Top 3 Emojis by Author</Typography>
          {analyticsData?.reactions?.top_emojis_by_author && Object.keys(analyticsData.reactions.top_emojis_by_author).length > 0 ? (
            <TableContainer>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Author ID</TableCell>
                    <TableCell>Top Reactions</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {Object.entries(analyticsData.reactions.top_emojis_by_author).map(([authorId, emojis]) => (
                    <TableRow key={authorId}>
                      <TableCell component="th" scope="row" sx={{ fontFamily: 'monospace', fontSize: '0.8rem', overflowWrap: 'break-word', maxWidth: '150px' }}>{authorId}</TableCell>
                      <TableCell>{emojis.map(e => `${e.emoji} (${e.count})`).join(', ')}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          ) : <Typography variant="body2" color="text.secondary">No data</Typography>}
        </Paper>
      </Grid>
    </Grid>
  );

  const renderHourlyChart = () => (
    <Paper sx={{ p: 2, height: 400 }}>
      <Typography variant="h6" gutterBottom>Hourly Activity</Typography>
      {analyticsData?.message_counts?.by_hour ? (
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={Object.entries(analyticsData.message_counts.by_hour)}>
            <XAxis dataKey="0" tickFormatter={(v) => formatHour(parseInt(v as string, 10))} />
            <YAxis />
            <Tooltip />
            <Line type="monotone" dataKey="1" stroke="#8884d8" />
          </LineChart>
        </ResponsiveContainer>
      ) : <Typography variant="body2" color="text.secondary">No data</Typography>}
    </Paper>
  );

  const renderAwardCard = (title: string, award: { winner: string | null; count: number }) => (
    <Grid item xs={12} sm={6} md={4} key={title}>
      <Paper sx={{ p: 2, height: '100%', display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
        <Typography variant="h6" component="div" sx={{ textAlign: 'center' }}>{title}</Typography>
        {award.winner ? (
          <>
            <Typography variant="body1" color="text.secondary" sx={{ fontFamily: 'monospace', fontSize: '0.8rem', overflowWrap: 'break-word', my: 1, maxWidth: '100%' }}>
              {award.winner}
            </Typography>
            <Typography variant="h5" component="div" sx={{ fontWeight: 'bold' }}>
              {award.count}
            </Typography>
          </>
        ) : (
          <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
            No data
          </Typography>
        )}
      </Paper>
    </Grid>
  );

  const renderAwards = () => {
    if (!analyticsData?.awards) return null;

    const awardDisplayTitles: Record<keyof typeof analyticsData.awards, string> = {
      most_messages_sent: "Most Messages Sent",
      most_reactions_given: "Most Reactions Given",
      most_reactions_received: "Most Reactions Received",
      most_mentioned: "Most Mentioned",
      most_eggplants_sent: "Most 🍆 Sent",
      most_media_sent: "Most Media Sent",
    };

    return (
      <Box sx={{ mt: 4 }}>
        <Typography variant="h5" gutterBottom>🏆 Awards 🏆</Typography>
        <Grid container spacing={3}>
          {Object.entries(analyticsData.awards).map(([key, award]) =>
            renderAwardCard(awardDisplayTitles[key as keyof typeof analyticsData.awards], award)
          )}
        </Grid>
      </Box>
    );
  };

  // ---------------------------------------------------------------------------
  // JSX
  // ---------------------------------------------------------------------------
  return (
    <Box sx={{ p: 4 }}>
      {/* Upload controls */}
      <Box sx={{ display: 'flex', gap: 2, alignItems: 'center', mb: 4 }}>
        <Button variant="contained" component="label" disabled={loading}>
          Select Database
          <input type="file" accept=".db,.sqlite" hidden onChange={handleFileChange} />
        </Button>
        <Button variant="contained" color="secondary" onClick={handleUploadAndAnalyse} disabled={loading || !file}>
          {loading ? 'Processing…' : 'Upload & Analyse'}
        </Button>
        {loading && <CircularProgress size={24} />}
      </Box>

      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}

      {analyticsData && (
        <>
          <Grid container spacing={3}>
            <Grid item xs={12} md={4}>
              {renderTopConversations()}
            </Grid>
            <Grid item xs={12} md={8}>
              {renderConversationFilter()}
              {renderKpiSummary()}
            </Grid>
          </Grid>
          <Grid container spacing={3} sx={{ mt: 2 }}>
            <Grid item xs={12} md={8}>
              {renderDailyChart()}
            </Grid>
            <Grid item xs={12} md={4}>
              {renderHourlyChart()}
            </Grid>
            <Grid item xs={12}>
              {renderReactionAnalytics()}
            </Grid>
            <Grid item xs={12}>
              {renderAwards()}
            </Grid>
          </Grid>
        </>
      )}
    </Box>
  );
};

export default Dashboard;
