): Promise<AnalyticsData> {
  try {
    const db = await createDatabaseFromBuffer(dbBuffer);
    const buildWhereClause = (tableAlias?: string, idColumn: string = 'conversationId') => {
      if (!conversationIds || conversationIds.length === 0) return '';
