      reactions: { total_reactions: 0, top_emojis: [], top_emojis_by_author: {} },
      awards: {
        most_messages_sent: { winner: null, count: 0 },
        most_reactions_given: { winner: null, count: 0 },
        most_reactions_received: { winner: null, count: 0 },
        most_mentioned: { winner: null, count: 0 },
        most_eggplants_sent: { winner: null, count: 0 },
        most_media_sent: { winner: null, count: 0 },
      },
      userNamesById: {},
    };

    // Build user ID to profileFullName mapping
    const userNameResults = db.exec(`SELECT serviceId, profileFullName FROM conversations WHERE serviceId IS NOT NULL AND profileFullName IS NOT NULL`);
    if (userNameResults[0]) {
